<!-- Tree --><link rel="stylesheet" href="<?php echo ADMIN_LAYOUT_PATH; ?>/css/tree.css"><script src="<?php echo ADMIN_LAYOUT_PATH; ?>/js/tree.js"></script><!-- custom js page --><script src="<?php echo ADMIN_LAYOUT_PATH; ?>/js/menu.js"></script><link rel="stylesheet" href="<?php echo ADMIN_LAYOUT_PATH; ?>/css/menu.css"><link rel="stylesheet" href="<?php echo ADMIN_LAYOUT_PATH; ?>/css/jquery.nestable.css"><script src="<?php echo ADMIN_LAYOUT_PATH; ?>/js/jquery.nestable.js"></script><script>$(document).ready(function(){    var updateOutput = function(e)    {        var list   = e.length ? e : $(e.target),            output = list.data('output');        if (window.JSON) {            output.val(window.JSON.stringify(list.nestable('serialize')));		} else {			$.notify('Trình duyệt của bạn cần phải hỗ trợ JSON', { globalPosition: 'top right',className: 'warning' } );        }    };    $('#nestable').nestable().on('change', updateOutput);    updateOutput($('#nestable').data('output', $('#nestable-output')));});</script><div class="row" >	<?php if(hm_get('mes')=='add_success'){ ?>		<div class="alert alert-success" role="alert"><?php echo _('Đã tạo trình đơn, bây giờ bạn có thể chỉnh sửa nó'); ?></div>	<?php } ?>	<div class="col-md-12">		<h1 class="page_title"><?php echo _('Chỉnh sửa trình đơn'); ?></h1>	</div>		<div class="col-md-12">			<div class="row admin_fullbar_box">					<div class="col-md-3 menu_select_choise">				<?php menu_select_choise(hm_get('id')); ?>			</div>			<div class="col-md-3">				<div class="form_new_menu"> 					<input class="new_menu col-md-9" name="name" placeholder="Tạo một trình đơn mới">					<button name="submit" type="button" class="btn btn-default col-md-3 submit_new_menu">Tạo</button>				</div>			</div>			<div class="col-md-3">				<div class="form_new_menu"> 					<input class="form-control" name="name" value="[shortcode=menu&menu_id=<?php echo hm_get('id'); ?>]">				</div>			</div>			<div class="col-md-3">				<div class="form_new_menu"> 					<button data-id="<?php echo hm_get('id'); ?>" class="btn btn-primary delete_menu_btn"><?php echo _('Xóa trình đơn'); ?></button>				</div>			</div>					</div>				<div class="row admin_fullbar_box">						<form action="?run=menu_ajax.php&id=<?php echo hm_get('id'); ?>&action=edit" method="post" class="ajaxForm ajaxFormmenuEdit">				<div class="col-md-4">									<?php					foreach($args_tax as $key => $val){					?>					<div class="menu_object_box">						<div class="menu_object_tab btn-default">							<span><?php echo $val['taxonomy_name']; ?></span>						</div>						<ul class="menu_object_list taxnomy_list tree">						<?php							$args['key']=$key;							taxonomy_checkbox_list($args); 						?>						</ul>					</div>					<?php					}					?>										<div class="form-group"> 						<button type="submit" class="btn btn-primary"><?php echo _('Thêm vào trình đơn'); ?></button>					</div>				</div>			</form>									<div class="col-md-8">								<div class="form-group save_menu_order"> 					<textarea id="nestable-output" style="display:none"></textarea>					<button data-id="<?php echo hm_get('id'); ?>" type="submit" class="btn btn-primary save-menu-order"><?php echo _('Lưu thứ tự sắp xếp'); ?></button>				</div>								<div class="dd" id="nestable">					<ol class="dd-list">												<?php nestable($args_menu); ?>											</ol>				</div>			</div>					</div>			</div></div><!-- Modal --><div class="modal fade" id="media_box_modal" tabindex="-1" role="dialog" aria-labelledby="media_box_modalLabel" aria-hidden="true">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span>                </button>                <h4 class="modal-title" id="media_box_modalLabel"><?php echo _('Thư viện'); ?><