<?php/**  * Tệp tin xử lý giao diện trong admin * Vị trí : admin/theme.php  */if ( ! defined('BASEPATH')) exit('403');/** gọi tệp tin admin base */require_once( dirname( __FILE__ ) . '/admin.php' );/** gọi model xử lý theme */require_once( dirname( __FILE__ ) . '/theme/theme_model.php' );$key=hm_get('key');$id=hm_get('id');$action=hm_get('action');switch ($action) {    case 'add':					function admin_content_page(){			require_once(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'theme_add.php');		}		    break;    default :				/** Hiển thị giao diện tất cả theme */		function admin_content_page(){			require_once(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'theme_all.php');		}}/** fontend */require_once(BASEPATH . HM_ADMINCP_DIR . '/' . LAYOUT_DIR . '/' . 'layout.php');