<?php/**  * Xử lý menu * Vị trí : hm_include/menu.php  */if ( ! defined('BASEPATH')) exit('403');/** * Gọi thư viện menu */require_once(BASEPATH . HM_INC . '/menu/hm_menu.php');/** * Khởi tạo class */$hmmenu = new menu;function get_men_val( $args=array() ){	hook_filter('before_get_men_val',$args);		global $hmmenu;		hook_action('get_men_val');		$return = $hmmenu->get_men_val($args);		$return = hook_filter('get_men_val',$return);		return $return;}function register_menu_location( $args=array() ){	hook_filter('before_register_menu_location',$args);		global $hmmenu;		hook_action('register_menu_location');		$return = $hmmenu->register_menu_location($args);		$return = hook_filter('register_menu_location',$return);		return $return;}function menu_location($menu_location_name = NULL){		hook_filter('before_menu_location',$menu_location_name);		global $hmmenu;		hook_action('menu_location');		$menu_location = $hmmenu->menu_location;		if(isset($menu_location[$menu_location_name])){				$return = $hmmenu->menu_location($menu_location[$menu_location_name]);			$return = hook_filter('menu_location',$return);			return $return;			}else{				return FALSE;			}	}